temp2 = (ip[12] * sinpi8sqrt2)>>16;
d1 = temp1 + temp2;

op[shortpitch*0] = a1+d1;
op[shortpitch*3] = a1-d1;
op[shortpitch*1] = b1+c1;
op[shortpitch*2] = b1-c1;

  ip++;
  op++;
}
